mapper.select:
select 

from amc_req_hst
where CONSUMER_TRANS_ID = #{consumerTransId,jdbcType=VARCHAR}
  and SYS_TRANS_ID = #{sysTransId,jdbcType=VARCHAR}

mapper.delete:
delete from amc_req_hst
where CONSUMER_TRANS_ID = #{consumerTransId,jdbcType=VARCHAR}
  and SYS_TRANS_ID = #{sysTransId,jdbcType=VARCHAR}

mapper.insert:
insert into amc_req_hst (CONSUMER_TRANS_ID, SYS_TRANS_ID, CHANNEL_ID, 
  CARD_NO, B003, REF_NBR, 
  B039, RESP_DESC, ORIG_DATA, 
  MSG_CONTENT, CREATED_DATETIME, LAST_MODIFIED_DATETIME
  )
values (#{consumerTransId,jdbcType=VARCHAR}, #{sysTransId,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, 
  #{cardNo,jdbcType=VARCHAR}, #{b003,jdbcType=VARCHAR}, #{refNbr,jdbcType=VARCHAR}, 
  #{b039,jdbcType=CHAR}, #{respDesc,jdbcType=VARCHAR}, #{origData,jdbcType=VARCHAR}, 
  #{msgContent,jdbcType=VARCHAR}, #{createdDatetime,jdbcType=TIMESTAMP}, #{lastModifiedDatetime,jdbcType=TIMESTAMP}
  )
