mapper.select:
select 

from amc_transfer_trace
where SYS_TRANS_ID = #{sysTransId,jdbcType=VARCHAR}
  and CONSUMER_TRANS_ID = #{consumerTransId,jdbcType=VARCHAR}

mapper.delete:
delete from amc_transfer_trace
where SYS_TRANS_ID = #{sysTransId,jdbcType=VARCHAR}
  and CONSUMER_TRANS_ID = #{consumerTransId,jdbcType=VARCHAR}
